Alteryx.Gui.BeforeLoad = function(manager, AlteryxDataItems){
  console.log("Before Load ----");
};

Alteryx.Gui.AfterLoad = function(manager){
  console.log("After Load ----");
  controlDisplayIntermediate(manager)
  displayTarget("parametricSampling", 'samplingMode', function(v){return v === 'parametric'})
  displayTarget("dataSampling", 'samplingMode', function(v){return v === 'data'})
  
  displayTarget("div-distToFit", "samplingStrategy", function(v){return v === 'dist'})
};

